package com.wraper.app.gui.elements.dataoutput;import com.wraper.app.selenium.E2ESeleniumHelper;import com.wraper.framework.guielement.Textual;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.support.pagefactory.ByChained;import static org.junit.jupiter.api.Assertions.assertEquals;public class GuiSimpleOutput implements Textual {    private E2ESeleniumHelper gui;    private WebDriver driver;    private final By byLable;    private final By byId;    private By mainElement;    protected GuiSimpleOutput(WebDriver pWebDriverProvider, By mainElement) {        this.mainElement = mainElement;        byLable = new ByChained(mainElement, By.tagName("label"));        byId = new ByChained(mainElement, By.tagName("span"));        gui = E2ESeleniumHelper.of(pWebDriverProvider);        this.driver = pWebDriverProvider;    }    public static GuiSimpleOutput of(WebDriver pWebDriverProvider, By byElement) {        return new GuiSimpleOutput(pWebDriverProvider, byElement);    }    @Override    public String getText() {        return driver.findElement(mainElement).getText();    }    @Override    public String getValue() {        return driver.findElement(mainElement).getText();    }    @Override    public void assertText(String text) {        assertEquals(text, getText());    }    @Override    public void assertTextCaseInsensitive(String text) {        assertEquals(text.toUpperCase(), getText().toUpperCase());    }    public void assertLabel(String label){        assertEquals(label, driver.findElement(byLable).getText());    }    public void assertTextId(String label){        assertEquals(label, driver.findElement(byId).getText());    }}