package com.wraper.app.gui.pages.facebook;import com.wraper.app.gui.elements.clickable.GuiButtonElement;import com.wraper.app.gui.elements.clickable.GuiLinkElement;import com.wraper.app.gui.elements.datainput.GuiInputElement;import com.wraper.app.gui.pages.base.E2EBasePage;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.support.pagefactory.ByChained;import static org.junit.jupiter.api.Assertions.assertTrue;public class FacebookLoginPage extends E2EBasePage {    public  By                          byContainer = By.id("content");    public GuiInputElement              inpUser;    public GuiInputElement              inpPass;    public GuiButtonElement             btnLogin;    public GuiButtonElement             btnCreate;    public GuiLinkElement               lnkPassForgot;    public FacebookLoginPage(WebDriver driverProvider) {        super(driverProvider);        inpUser = GuiInputElement.of(driverProvider, By.className("_6lux"));        inpPass = GuiInputElement.of(driverProvider, new ByChained(By.className("_6lux"),By.id("passContainer")));        btnLogin = GuiButtonElement.of(driverProvider, By.name("login"));        btnCreate = GuiButtonElement.of(driverProvider, new ByChained(By.className("_6ltg"),By.tagName("a")));        lnkPassForgot = GuiLinkElement.of(driverProvider, By.className("_6ltj"));    }    public static FacebookLoginPage of(WebDriver driverProvider){return new FacebookLoginPage(driverProvider); }    @Override    public FacebookLoginPage waitUntilReady() {        return null;    }    @Override    public void waitUntilVisible(){gui().waitForCondition(()-> isVisible());}    @Override    public boolean isVisible(){        return inpUser.isEnabled();    }    @Override    public void assertPageIsShown() {        assertTrue(isVisible());    }}