package com.wraper.testcases;import com.wraper.app.gui.pages.DialogFactory;import com.wraper.framework.logger.MyLogger;import org.junit.jupiter.api.AfterEach;import org.junit.jupiter.api.BeforeEach;import org.openqa.selenium.WebDriver;import org.openqa.selenium.chrome.ChromeDriver;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;public class BaseTest  {    public  WebDriver driver;    public DialogFactory dialogFactory;    public BaseTest() {        this.driver = new ChromeDriver();        this.dialogFactory = DialogFactory.of(driver);    }    public static BaseTest of(){return new BaseTest();}    public void setDriver(WebDriver driver) {        this.driver = driver;    }    public void initPages(){}    public  WebDriver getDriver() {        return driver;    }    @BeforeEach    public void beforeTestRun(){        initPages();        driver.manage().window().fullscreen();        driver.manage().deleteAllCookies();        start();    }    @AfterEach    public void afterTestRun(){        finish();    }    private Class returnClassname(){        return getClass();    }    public void start(){        MyLogger.resInfoLog(BaseTest.class, "Test Case "+returnClassname()+" started at: "+LocalDateTime.now().format(DateTimeFormatter.ISO_DATE_TIME));    }    public void finish(){        driver.quit();        MyLogger.resInfoLog(BaseTest.class, "Test Case "+returnClassname()+" ended at: "+LocalDateTime.now().format(DateTimeFormatter.ISO_DATE_TIME));    }    public void testActivity(String activity){        MyLogger.resInfoLog(BaseTest.class, "Test Step ----=== "+activity+" ===----");    }    public void testSteps(int step){        MyLogger.resInfoLog(BaseTest.class, "Test Step ----==+ "+step+" +==----");    }}