package com.wraper.testcases.seleniumeasy;import com.wraper.app.gui.pages.easyseleium.SimpleFormDemoPage;import com.wraper.testcases.BaseTest;import org.openqa.selenium.By;public class BaseEasySelenium extends BaseTest {    public SimpleFormDemoPage pgSimpleform;    public BaseEasySelenium() {        driver.get("https://www.seleniumeasy.com/test/basic-first-form-demo.html");    }    @Override    public void initPages(){        super.initPages();        pgSimpleform = dialogFactory.simplFormPage();    }    public void closeAd(){        if (driver.findElement(By.cssSelector("#at-cv-lightbox-close")).isEnabled())        driver.findElement(By.cssSelector("#at-cv-lightbox-close")).click();    }}